mapper.select:
select code, current_price
from df_a_stock_detail
where code = #{code}
  and #{date} >= ds
  and current_price > 0
order by ds desc limit #{day};

select code, current_price
from df_a_stock_detail
where code = #{code}
  and #{date} >= ds
  and current_price > 0
order by ds desc limit #{day};

select code, current_price, highest, lowest
from df_a_stock_detail
where code = #{code}
  and current_price > 0
order by ds desc limit #{day};
